            public decimal GetDiscountAmount()
            {
                log.LogMethodEntry();
                decimal result = 0;
                if (TransactionDiscountsDTOList != null)
                {
                    foreach (TransactionDiscountsDTO transactionDiscountsDTO in TransactionDiscountsDTOList)
                    {
