        public string GetDiscountsDisplayCharacter(int count)
        {
            log.LogMethodEntry(count);

            string returnValue = "*";
            switch (count % 5)
            {
                case 0:
                    returnValue = "*";
                    break;
                case 1:
                    returnValue = "^";
                    break;
                case 2:
                    returnValue = "#";
                    break;
                case 3:
                    returnValue = "@";
                    break;
                case 4:
                    returnValue = "$";
                    break;
            }

            log.LogMethodExit(returnValue);
            return returnValue;
        }

