                    SqlCommand Invcmd = Utilities.getCommand(SQLTrx);
                    Utilities.executeNonQuery(@"Update trx_lines 
                                                set OriginalLineId = @OriginalLineId
                                                Where TrxId = @TrxId AND LineId = @LineId",
