                    TrxLines[i].card.addTime = 0;
                    TrxLines[i].card.addCreditPlusCardBalance = 0;
                    TrxLines[i].card.addTicketCount = 0;
                    TrxLines[i].card.TotalRechargeAmount = 0;

                    for (int j = i; j < TrxLines.Count; j++)
