                            AccountBL accountBL = new AccountBL(Utilities.ExecutionContext, TrxLines[i].card.card_id, false, false, TrxTransacation);
                            if (accountBL.IsAccountUpdatedByOtherTransactions(GameCardReadTime, TrxTransacation))
