                log.LogVariableState("@Remarks", dataRow["Remarks"]);

                if (Utilities.ParafaitEnv.IsCorporate == false || Utilities.ParafaitEnv.SiteId <= 0)
                {
                    creditPlusCmd.Parameters.AddWithValue("@SiteId", DBNull.Value);
