                //log.LogVariableState("ForMembershipOnly", (Line.ForMembershipOnly == true ? (object)Line.ForMembershipOnly : DBNull.Value));
                if (!String.IsNullOrEmpty(Line.ForMembershipOnly) && Line.ForMembershipOnly == "Y")
