                //log.LogVariableState("ExpireWithMembership", (Line.ExpireWithMembership == true ? (object)Line.ExpireWithMembership : DBNull.Value));
                if (!String.IsNullOrEmpty(Line.ExpireWithMembership) && Line.ExpireWithMembership == "Y")
