	                                        min(case when pt.CardSale = 'Y' then 'Card' else 'Manual' end) ProductType,
	                                        min(ISNULL(l.ParentLineId, -1)) ParentLineId,
	                                        ISNULL(remarks, '') remarks,
	                                        min(l.lineid) DBLineId,
										    min(case when @bookingTrx = 1
