																 where trxid = @TrxId 
																group by trxid, lineid) tdamount on l.trxid = tdamount.TrxId and l.LineId = tdamount.LineId
                                               left outer join tax linetax on l.tax_id = linetax.tax_id
                                                        left outer join (SELECT lockername, la.trxid, la.trxLineId
