					                                    LEFT OUTER JOIN (SELECT atb.trxid, atb.lineid, ap.PlayName + 
						                                                                                ':' + replace(Convert(varchar(11), da.ScheduleDateTime, 106), ' ', '-') + ' ' + LTRIM(RIGHT(CONVERT(VARCHAR(20), da.ScheduleDateTime, 100), 7)) +
