            object o = Utilities.executeScalar("select LastUpdateTime from trx_header where trxId = @trxId", SQLTrx,
                                                new SqlParameter("@trxId", trxId));

            log.LogVariableState("@trxId", trxId);

            if (o != null && o != DBNull.Value)
